using System;using System.Text;namespace Microsoft.Xna.Framework.Graphics{	internal static class VSBasicVcNoFog	{		internal static string BasicEffectVertexCode ()		{						string code =				// Set up our uniform variables				"uniform mat4 WorldViewProj;\n"				+ "uniform mat4 World;\n"				+ "uniform mat4 WorldView;\n"				+ "void main(){\n"				// set up references to our uniforms				+ "mat4 wvp = WorldViewProj;\n"				+ "mat4 wrld = World;\n"				+ "mat4 wrldView = WorldView;\n"				// Made sure we set the FrontColor for our vertexes				+ "gl_FrontColor = gl_Color;\n"				// Do our transform				+ "gl_Position = gl_ModelViewProjectionMatrix * gl_Vertex;"			//+ "gl_Position = WorldViewProj * gl_Vertex;\n"			//+ "gl_Position = ftransform();"				+ "\n}";			return code;		}	}}